css:
#proposta {
  position: absolute;
  margin-left: 8%;
  max-width: 350px;
  margin-top:5%;
}

#propostaTitle,
#propostaSubtitle {
  text-shadow: 1px 1px white;
}

#callToAction {
  background-color: var(--white);
  border: 1px solid var(--white);
  margin: 25px auto auto 0;
  min-width:150px;
  padding: 10px;
}

#callToAction a {
  color: var(--primary-yiq);
}

#callToAction:hover {
    background-color: var(--white);
}

#callToAction:hover a {
    color: var(--white);
}

#beneficios {
  padding: 30px 20px;
  background-color: var(--primary);
  color: var(--primary-yiq);
}

#beneficios p:not(:last-child) {
  padding-bottom: 20px;
  border-bottom: 1px solid #C8C8C8;

}

#ctaContainer {
  display: flex;
  width: 100%;
  margin-top: 20px;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

#ctaContainer button {
  margin: 10px 0 20px 0;
}

#provaSocial {
  padding: 30px 20px;
  background-color: #C8C8C8;
  font-size: 22px;
}

#provaSocial a {
  color: indigo;
  text-decoration: underline;
}

@media(max-width: 575px) {
  #cabecalho img {
    content: url('file:///C:/Users/Pichau/Downloads/Formas%20Org%C3%A2nicas%20Azul%20e%20Rosa%20Minimalista%20%20Linha%20de%20Roupas%20Sustent%C3%A1veis%20%20Post%20Para%20Facebook.png');
    width: 100%;
  }
}

@media(min-width:575px) and (max-width: 991px) {
    #proposta .cta-container {
        display:flex;
        margin-top: 0;
        gap: 10px;
    }
    #callToAction {
        margin-bottom: 12px;
        margin-top: auto;
    }
}
